          const metrics = [
            { key: "comp", zKey: "cz", label: "Composite Δ", zLabel: "Composite Z" },
            { key: "unc", zKey: "uz", label: "Uncertainty Δ", zLabel: "Uncertainty Z" },
            { key: "neg", zKey: "nz", label: "Negative Δ", zLabel: "Negative Z" },
          ];

          const corrResults = [];
          for (const m of metrics) {
            for (const w of windows) {
              const valid = corrDataset.filter(r => r[`r${w}`] !== undefined && r[m.key] !== undefined);
              const xs = valid.map(r => r[m.key] * 1000); // scale for readability
              const ys = valid.map(r => r[`r${w}`]);
              const { r, p, n } = pearson(xs, ys);
              corrResults.push({ metric: m.label, window: `+${w}w`, weeks: w, r, p, n, sig: p < 0.1 });

              // Also z-score correlation
              const xz = valid.map(r => r[m.zKey]);
              const yz = valid.map(r => r[`r${w}`]);
              const zCorr = pearson(xz, yz);
              corrResults.push({ metric: m.zLabel, window: `+${w}w`, weeks: w, r: zCorr.r, p: zCorr.p, n: zCorr.n, sig: zCorr.p < 0.1 });
            }
          }

          // Chart data: scatter plot of composite z vs forward returns
          const scatterWindow = 13; // 13 weeks = 1 quarter forward
          const scatterData = corrDataset
            .filter(r => r[`r${scatterWindow}`] !== undefined)
            .map(r => ({ name: r.period, x: r.cz, y: r[`r${scatterWindow}`], dt: r.dt === "annual_mda" ? "Annual" : "Interim" }));

          // For the overlay chart: tone timeline with price
          const overlayData = toneFilings.map(f => {
            const pp = findPrice(f.p);
            return { p: f.p.slice(2,7), tone: +(f[metricKey]*1000).toFixed(2), z: f[zKey], price: pp ? pp.c : null, period: f.p, dt: DT_LABELS[f.dt] };
          });

