                  return <div style={{ padding: "5px 0", fontWeight: 800, fontSize: 9, borderTop: `2px solid ${C.tx}`, marginTop: 2 }}>Avg +13w return: <span style={{ color: +avg < 0 ? C.r : C.g }}>{avg}%</span> (n={up.length})</div>;
